settings.custom_liquid:
{% if product.metafields.godine.subtitle %}
    <h2>{{ product.metafields.godine.subtitle }}</h2>
{% endif %}

{% if product.metafields.godine.author_1_description %}
    {% assign author_group_1 = product.metafields.godine.author_1_collections.value %}

    {{ product.metafields.godine.author_1_description }}
    {% for author in author_group_1 %}
        <a href="{{ author.url }}">{{ author.title | replace: 'Author:', '' }}</a>
        {%- if forloop.last != true and forloop.length > 0 %}, {% endif %}
    {% endfor %}
{% endif %}

{% if product.metafields.godine.author_2_description %}
    <br>
    {% assign author_group_2 = product.metafields.godine.author_2_collections.value %}

    {{ product.metafields.godine.author_2_description }}
    {% for author in author_group_2 %}
        <a href="{{ author.url }}">{{ author.title | replace: 'Author:', '' }}</a>
        {%- if forloop.last != true and forloop.length > 0 %}, {% endif %}
    {% endfor %}
{% endif %}

{% if product.metafields.godine.author_3_description %}
    <br>
    {% assign author_group_3 = product.metafields.godine.author_3_collections.value %}

    {{ product.metafields.godine.author_3_description }}
    {% for author in author_group_3 %}
        <a href="{{ author.url }}">{{ author.title | replace: 'Author:', '' }}</a>
        {%- if forloop.last != true and forloop.length > 0 %}, {% endif %}
    {% endfor %}
{% endif %}


{% if product.metafields.godine.series_collection %}

{% assign imprint = product.metafields.godine.series_collection.value %}
<p>
Part of: <a href="{{ imprint.url }}">{{ imprint.title | replace: 'Series: ', '' }}</a>
</p>
{% endif %}

settings.content:
{% if product.metafields.godine.imprint_collection %}
  {% assign imprint = product.metafields.godine.imprint_collection.value %}
  <p>
    <a href="{{ imprint.url }}">{{ imprint.title | replace: 'Imprint: ', '' }}</a>
  </p>
{% endif %}

{% for variant in product.variants %}
  <div style="margin-bottom: 1.5rem">
    <strong>{{ variant.options }}</strong><br>

    {% if variant.barcode %}
      ISBN: {{ variant.barcode -}}
      <br>
    {% endif %}

    {% if variant.metafields.godine.page_count %}
      Pages: {{ variant.metafields.godine.page_count -}}
      <br>
    {% endif %}

    {% if variant.metafields.godine.size %}
      Size: {{ variant.metafields.godine.size }}
      <br>
    {% endif %}

    {% if variant.metafields.godine.published_date %}
      Published: {{ variant.metafields.godine.published_date }}
    {% endif %}
  </div>
{% endfor %}
