settings.custom_liquid:
{% if product.metafields.godine.author_collection %}
    {% assign author = product.metafields.godine.author_collection.value %}
    by <a href="{{ author.url }}">{{ author.title | replace: 'Author/Contributor:', '' }}</a>
{% endif %}

{% if product.metafields.godine.contributor_1_collection %}
    <br>
    {% assign contributor_1 = product.metafields.godine.contributor_1_collection.value %}
    {{ product.metafields.godine.contributor_1_description }} <a href="{{ contributor_1.url }}">{{ contributor_1.title | replace: 'Author/Contributor:', '' }}</a>
{% endif %}

{% if product.metafields.godine.contributor_2_collection %}
    <br>
    {% assign contributor_2 = product.metafields.godine.contributor_2_collection.value %}
    {{ product.metafields.godine.contributor_2_description }} <a href="{{ contributor_2.url }}">{{ contributor_2.title | replace: 'Author/Contributor:', '' }}</a>
{% endif %}

{% if product.metafields.godine.contributor_3_collection %}
    <br>
    {% assign contributor_3 = product.metafields.godine.contributor_3_collection.value %}
    {{ product.metafields.godine.contributor_3_description }} <a href="{{ contributor_3.url }}">{{ contributor_3.title | replace: 'Author/Contributor:', '' }}</a>
{% endif %}

settings.content:
{% for variant in product.variants %}
<div style="margin-bottom: 1.5rem">

<strong>{{ variant.options }}</strong><br>

ISBN: {{ variant.barcode}}<br>

Pages: {{ variant.metafields.godine.page_count }}<br>

Size: {{ variant.metafields.godine.size }}
</div>
{% endfor %}
